import React, { useEffect, useState } from 'react'
import Input from '../../components/Input';
import Button from '../../components/Button';
import { useNavigate, useParams } from 'react-router-dom';
import { useTeamData } from '../../context/TeamProvider';

export default function AddTeam() {
    const navigate = useNavigate()
    // Get Team ID
    const { id } = useParams()
    //Check is ID there or not
    const isID = !!id
    // Get Data from Team Provider
    const { selectedTeam, create, update, getTeamById } = useTeamData()
    // Form State
    const [Form, setForm] = useState({ teamName: '' })

    useEffect(() => {
        if (isID) {
            getTeamById(id)
        }
        // eslint-disable-next-line
    }, []);
    useEffect(() => {
        // Set Form
        if (selectedTeam && isID) {
            setForm((prevState) => ({
                ...prevState,
                teamName: selectedTeam.teamName
            }));
        }
    }, [selectedTeam, isID])

    const handleChange = (e) => {
        const { name, value } = e.target;
        setForm((prevState) => ({ ...prevState, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault()
        isID ?
            // Update Team
            update(id, Form)
                .then(res => {
                    alert(res)
                    navigate('/team')
                })
                .catch(err => alert(err))
            :
            // Create Team
            create(Form)
                .then(res => {
                    alert(res)
                    navigate('/team')
                })
                .catch(err => alert(err))
    }

    return (
        <section>
            <div className="bg-gray-50 min-h-screen pt-20">
                <div className="container w-9/12 mx-auto px-5">
                    <h1 className="text-4xl font-medium py-7 text-center">Team {isID ? "Updation" : "Creation"}</h1>
                    <div className='bg-white p-5 shadow-lg rounded-lg '>
                        <form autoComplete="off" onSubmit={handleSubmit}>
                            <Input type="text" name="teamName" value={Form.teamName} onChange={handleChange} minLength={8} title={'Team Name'} />
                            <Button type="submit" label={'Submit'} />
                        </form>
                    </div>
                </div>
            </div>
        </section>
    )
}
